#include "ScalarConverter.hpp"

void	print_test(std::string str) {
	std::cout << COLOR(36) << "/********** Test: \"" << str << "\" **********/";
	std::cout << COLOR(0) << std::endl;
}

int	main(void) {
	std::string str[8] = {"0", "nan", "42.0f", 
		"65436524645456", "-453.54325",
		"-.0",
		"~", "-128"};
	
	for (size_t i = 0; i < 8; i++) {
		print_test(str[i]);
		ScalarConverter::convert(str[i]);
		std::cout << std::endl;
	}
	return (0);
}